var app = angular.module("techWishList", ["ngRoute"]);

app.config(["$routeProvider", function ($routeProvider) {
    $routeProvider
        .when("/cptech", {
            templateUrl: "cptech/cptech.html",
            controller: "mainCntrl"
        })

    .when("/other", {
        templateUrl: "other/other.html",
        controller: "secondCntrl"
    })

    .otherwise({
        redirectTo: "/cptech"
    })
}])

app.controller("mainCntrl", ["$scope", function ($scope) {
    $scope.items = [
                $scope.googleGlasses = {
            imgUrl: "http://www.techadvisor.co.uk/cmsdata/features/3527880/Google_Glass_UK_Explorer_Programme.jpg",
            name: "Google Glasses",
            price: "$199.99"
                },

                $scope.iBeer = {
            imgUrl: "http://www.lolwot.com/wp-content/uploads/2015/06/20-weirdest-and-pointless-phone-apps.jpg",
            name: "iBeer",
            price: "$759.99"
                },

                $scope.iPotty = {
            imgUrl: "data:image/jpeg;base64,",
            name: "iPotty",
            price: "$999.99"
                }

                    ];

            }])



app.controller("secondCntrl", ["$scope", function ($scope) {
    $scope.items = [
                $scope.nintendo = {
            imgUrl: "data:image/jpeg;base64,",
            name: "Nintendo",
            price: "$100.00"
                },

                $scope.drone = {
            imgUrl: "data:image/jpeg;base64,",
            name: "Adorama Camera",
            price: "$31,578.84"
                },

                $scope.roomba = {
            imgUrl: "data:image/jpeg;base64,",
            name: "iRobot Roomba",
            price: "$899.99"
                }

                    ];
            }])